// adapted from hexo/lib/plugins/helper/open_graph.js
const urlFn = require('url');
const moment = require('moment');
const { Component, Fragment } = require('inferno');
const { encodeURL, stripHTML, escapeHTML } = require('hexo-util');
const localeMap = {
    'en': 'en_US',
    'de': 'de_DE',
    'es': 'es_ES',
