    const { helper } = props;

    return {
        cssUrl: helper.cdn('social-share.js', '1.0.16', 'dist/css/share.min.css'),
        jsUrl: helper.cdn('social-share.js', '1.0.16', 'dist/js/social-share.min.js')
    };
});

